package wipro_java;

public class Largestnum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a = 30, b = 25, c = 20;

	        if (a >= b && a >= c)
	            System.out.println("Largest is: " + a);
	        else if (b >= a && b >= c)
	            System.out.println("Largest is: " + b);
	        else
	            System.out.println("Largest is: " + c);
	    }
	}


